mapper.sql:
and db_name = #{dbName}
and `table_name` = #{tableName}

    and `column_name` like concat('%', #{columnName}, '%')

mapper.select:
select id, db_name as dbName, `table_name` as tableName, `column_name` as columnName, column_type as columnType, column_length as columnLength, mapping_type as mappingType, column_desc as columnDesc, required_flag as requireFlag, null_able as nullAble, primary_key_flag as primaryKeyFlag, index_flag as indexFlag, index_type as indexType, index_desc as indexDesc, related_table as relatedTable, related_table_field as relatedTableField from table_logic_relation where 1 = 1
and `column_name` like concat('%', #{searchText}, '%')
and `column_name` != #{selectedColumnName}
order by ${sortName} ${order} limit #{start}, #{end}
